import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Home.css';

const Home = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logique d'inscription à implémenter
    console.log('Form submitted:', formData);
  };

  return (
    <div className="home-container">
      <section className="hero-section">
        <h1>Bienvenue sur TawjihiBot</h1>
        <p>Votre guide intelligent pour l'orientation scolaire et professionnelle</p>
      </section>

      <section className="features-section">
        <div className="feature-card">
          <h3>Orientation Personnalisée</h3>
          <p>Découvrez les filières qui correspondent à vos compétences et aspirations</p>
        </div>
        <div className="feature-card">
          <h3>Chatbot Intelligent</h3>
          <p>Posez vos questions et obtenez des réponses instantanées</p>
        </div>
        <div className="feature-card">
          <h3>Inscription aux Écoles</h3>
          <p>Accédez facilement aux inscriptions dans les établissements de votre choix</p>
        </div>
      </section>

      <section className="signup-section">
        <h2>Créer un compte</h2>
        <form onSubmit={handleSubmit} className="signup-form">
          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Mot de passe"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirmer le mot de passe"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="signup-button">S'inscrire</button>
        </form>
      </section>
    </div>
  );
};

export default Home;
